export const photoBaseUrl = "https://api.pexels.com/v1/";
export const videoBaseUrl = "https://api.pexels.com/videos/";
export const collectionBaseUrl = "https://api.pexels.com/v1/collections/";
export const testApiKey = process.env.API_KEY as string;
import { Params } from "./types";
import { photoBaseUrl, videoBaseUrl, collectionBaseUrl } from "./constants";

type AllowedTypes = "photo" | "video" | "collections";

const baseUrls: { [T in AllowedTypes]: string } = {
  photo: photoBaseUrl,
  video: videoBaseUrl,
  collections: collectionBaseUrl,
};

export default function createFetchWrapper(apiKey: string, type: AllowedTypes) {
  const options = {
    method: "GET",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
      "User-Agent": "Pexels/JavaScript",
      Authorization: apiKey,
    },
  };

  const baseUrl = baseUrls[type];

  return <T extends Params>(path: string, params?: T) =>
    fetch(`${baseUrl}${path}?${stringifyParams(params || {})}`, options).then(
      (response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }

        return response.json();
      }
    );
}

function stringifyParams<T extends Params>(params: T) {
  return Object.keys(params)
    .map((key) => `${key}=${params[key]}`)
    .join("&");
}
import createFetchWrapper from "./createFetchWrapper";
import { Collection, PaginationParams, ErrorResponse, Medium } from "./types";

type AllReturn =
  | {
      page: number;
      per_page: number;
      collections: Collection[];
    }
  | ErrorResponse;

type MediaReturn =
  | {
      page: number;
      per_page: number;
      total_results: number;
      media: (Medium & { type: "Video" | "Photo" })[];
    }
  | ErrorResponse;

type FeaturedReturn =
  | { page: number; per_page: number; collections: Collection[] }
  | ErrorResponse;

export default function generateCollectionEndpoints(apiKey: string) {
  const fetchWrapper = createFetchWrapper(apiKey, "collections");

  return {
    all(params: PaginationParams = {}): Promise<AllReturn> {
      return fetchWrapper("", params);
    },
    media({
      id,
      ...params
    }: PaginationParams & {
      id: string | number;
      type?: "photos" | "videos";
    }): Promise<MediaReturn> {
      return fetchWrapper(`${id}`, params);
    },
    featured(params: PaginationParams = {}): Promise<FeaturedReturn> {
      return fetchWrapper("featured", params);
    },
  };
}
import { Photos, Videos, ErrorResponse } from "./types";

/**
 * A helper function to determine if the object is a valid Photo response
 */
export function isPhotos(x: any): x is Photos {
  return !!(x && x.photos);
}

/**
 * A helper function to determine if the object is a valid Video response
 */
export function isVideos(x: any): x is Videos {
  return !!(x && x.videos);
}

/**
 * A helper function to determine if the object was an error response
 */
export function isError(x: any): x is ErrorResponse {
  return !!x.error;
}
import createFetchWrapper from "./createFetchWrapper";
import {
  Photo,
  PaginationParams,
  ErrorResponse,
  Photos,
  PhotosWithTotalResults,
} from "./types";
import { isPhotos } from "./typeCheckers";

type SearchReturn = PhotosWithTotalResults | ErrorResponse;
type CuratedReturn = Photos | ErrorResponse;
type ShowReturn = Photo | ErrorResponse;
type RandomReturn = Photo | ErrorResponse;

export default function generatePhotoEndpoints(apiKey: string) {
  const fetchWrapper = createFetchWrapper(apiKey, "photo");

  return {
    search(
      params: PaginationParams & { query: string }
    ): Promise<SearchReturn> {
      return fetchWrapper(`/search`, params);
    },
    curated(params: PaginationParams = {}): Promise<CuratedReturn> {
      return fetchWrapper(`/curated`, params);
    },
    show({ id }: { id: string | number }): Promise<ShowReturn> {
      return fetchWrapper(`/photos/${id}`);
    },
    async random(): Promise<RandomReturn> {
      const randomPage = Math.floor(1000 * Math.random());
      const response = await this.curated({ page: randomPage, per_page: 1 });

      if (isPhotos(response)) {
        return response.photos[0] as RandomReturn;
      }

      return response as ErrorResponse;
    },
  };
}
import createFetchWrapper from "./createFetchWrapper";
import {
  PaginationParams,
  ErrorResponse,
  Video,
  Videos,
  VideoFilterParams,
} from "./types";

type SearchReturn = Videos | ErrorResponse;
type PopularReturn = Videos | ErrorResponse;
type ShowReturn = Video;

export default function generatePhotoEndpoints(apiKey: string) {
  const fetchWrapper = createFetchWrapper(apiKey, "video");

  return {
    search(
      params: PaginationParams &
        VideoFilterParams & {
          query: string;
        }
    ): Promise<SearchReturn> {
      return fetchWrapper(`/search`, params);
    },
    popular(
      params: PaginationParams & VideoFilterParams = {}
    ): Promise<PopularReturn> {
      return fetchWrapper(`/popular`, params);
    },
    show({ id }: { id: string | number }): Promise<ShowReturn> {
      return fetchWrapper(`/videos/${id}`);
    },
  };
}
require("isomorphic-fetch");

import generateCollectionEndpoints from "./generateCollectionEndpoints";
import generatePhotoEndpoints from "./generatePhotoEndpoints";
import generateVideoEndpoints from "./generateVideoEndpoints";
import * as typeCheckers from "./typeCheckers";

export default function createClient(apiKey: string) {
  if (!apiKey || typeof apiKey !== "string") {
    throw new TypeError(
      `An ApiKey must be provided when initiating the Pexel's client.`
    );
  }

  return {
    typeCheckers,
    photos: generatePhotoEndpoints(apiKey),
    videos: generateVideoEndpoints(apiKey),
    collections: generateCollectionEndpoints(apiKey),
  };
}
