import styled from "styled-components";

function Upload() {
  const Text = styled.p`
    text-align: center;
    align-items: center;
  `;

  const DragBox = styled.div`
    border: solid black 1px;
    width: 80%;
    height: 300px;
    margin: auto;
  `;

  return (
    <div>
      <DragBox>
        <Text>Drag your photos into the box below!</Text>
      </DragBox>
      <button
        onClick={null}
        style={{
          margin: "auto",
          display: "flex",
          justifyContent: "center",
        }}
      >
        Upload
      </button>
    </div>
  );
}

export default Upload;
